this is the used function: 

function word2color($w){ 
        if (strlen($w)==0) return substr('00000' . dechex 
(mt_rand(0, 0xffffff)), -6); 
        while (strlen($w)<6) $w.=$w; 
        $minbrightness=1;  // range from 0 to 15, if this is  
0 then for ex. black is allowed 
        $max_brightness=13; // range from 0 to 15, if this  
is 15 then for ex. white is allowed 
        $plus_red=0;    // set one of these to set the  
probability of one of these colors higher 
        $plus_green=0; 
        $plus_blue=0; 
        for ($i=0; $i<6; $i++) { 
            #$r.= '">';// this is a depug mode, to see the  
color written 
            $plus=0; 
            if ($plus_red0 and $i==0) $plus=$plus_red; 
            if ($plus_green0 and $i==2)  
$plus=$plus_green; 
            if ($plus_blue0 and $i==4) $plus=$plus_blue; 
            $c=$w[round(strlen($w)/6*$i)]; 
            $dec=ord($c)%($max_brightness+$plus- 
$minbrightness) +$minbrightness+$plus; 
            if ($dec>$max_brightness-$minbrightness)  
$dec=$max_brightness-$minbrightness; 
            $r.= strtoupper( dechex($dec) ); 
        } 
        return $r; 
    }

